mapper.select:
select
	id,
	user_id,
	room_id,
	create_time,
	game_name,
	server_id,
	consume,
	round_count
from gmb_diamond_consume


		and id = #{id}


		and user_id = #{userId}


		and room_id = #{roomId}


		and create_time = #{createTime}


		and game_name = #{gameName}


		and server_id = #{serverId}


		and consume = #{consume}


		and round_count = #{roundCount}


		and create_time > #{startTime}


		and create_time < #{endTime}
